// JScript 文件
$(document).ready(function () {
    var wordInput = $("#txt_Username");

    wordInput.blur(function (event) {
        var wordText = $("#txt_Username").val();
        if (wordText == '') {
            $("#txt_Username").val("请输入邮箱或者手机号");
        }
    });

    wordInput.mousedown(function (event) {
        var wordText = $("#txt_Username").val();
        if (wordText == '请输入邮箱或者手机号') {
            $("#txt_Username").val("");
        }

    });
    $("#login_password_text2").focus(function () {
        var _obj = $(this);
        _obj.hide();
        _obj.prev().show();
        _obj.prev().focus();
    });

    $("#txt_Password").blur(function () {
        if ($(this).val() == '') {
            $(this).hide();
            $(this).next().show();
        }
    });

    $(function () {
        document.onkeydown = function (e) {
            var ev = document.all ? window.event : e;
            if (ev.keyCode == 13) {

                var username = $("#txt_Username").val();
                var userpass = $("#txt_Password").val();
                if (username == "请输入邮箱或者手机号" || username == "") {
                    $("#mode_tips").html("<span class='gtl_ico_hits'></span>请输入登录账号");
                    openStaticPopup();
//                    alert("请输入登录账号!");
                    return;
                }
                if (userpass == "") {
//                    alert("请输入登录密码!");
                    $("#mode_tips").html("<span class='gtl_ico_hits'></span>请输入登录密码");
                    openStaticPopup();
                    return;
                }
                var url = $("#hidUrl").val();
                var check = document.getElementById("cb_Check").checked;
                var timeout = 30;
                if (check == true) {
                    timeout = 5440;
                }
                $.ajax({
                    type: "POST",
                    url: "lib/loginHandler.ashx",
                    data: {
                        type: "1",
                        name: username,
                        pass: userpass,
                        timeout: timeout
                    },
                    cache: false,
                    beforeSend: function () {
                        $("#login").html("正在登录...");
                    },
                    success: function (result) {

                        if (result == '000') {
                            if (url == '') {
                                window.location.href = "index.html";
                            }
                            else {
                                window.location.href = url;
                            }
                        }
                        else {
                            $("#login").html("登 &nbsp; &nbsp; 录");
                            $("#mode_tips").html("<span class='gtl_ico_hits'></span>" + result + "");
                            openStaticPopup();
                        }
                    }
                });

            }
        }
    });

    $("#login").live("click", function () {
        var username = $("#txt_Username").val();
        var userpass = $("#txt_Password").val();
        if (username == "请输入邮箱或者手机号" || username == "") {
            //            alert("请输入登录账号!");
            $("#mode_tips").html("<span class='gtl_ico_hits'></span>请输入登录账号");
            openStaticPopup();
            return;
        }
        if (userpass == "") {
            //            alert("请输入登录密码!");
            $("#mode_tips").html("<span class='gtl_ico_hits'></span>请输入登录密码");
            openStaticPopup();
            return;
        }
        var url = $("#hidUrl").val();
        var check = document.getElementById("cb_Check").checked;
        var timeout = 30;
        if (check == true) {
            timeout = 5440;
        }
        $.ajax({
            type: "POST",
            url: "lib/loginHandler.ashx",
            data: {
                type: "1",
                name: username,
                pass: userpass,
                timeout: timeout
            },
            cache: false,
            beforeSend: function () {
                $("#login").html("正在登录...");
            },
            success: function (result) {

                if (result == '000') {
                    if (url == '') {
                        window.location.href = "index.html";
                    }
                    else {
                        window.location.href = url;
                    }
                }
                else {
                    $("#login").html("登 &nbsp; &nbsp; 录");
                    $("#mode_tips").html("<span class='gtl_ico_hits'></span>" + result + "");
                    openStaticPopup();
                }
            }
        });
    });

})
try {
    $.setupJMPopups({
        screenLockerBackground: "#003366",
        screenLockerOpacity: "0.5"
    });
} catch (e) { }

function openStaticPopup() {
    $.openPopupLayer({
        name: "myStaticPopup",
        width: 1000,
        target: "myHiddenDiv1"
    });
}